export default {
    state: {
        users:[]
    },
    getters: {
    },
    mutations: {
        updateUsers(state,users){
            state.users=users
        }
    },
    actions: {
        async fetchUsers(commit,getters,dispatch){
            const res = await fetch(
                'https://jsonplaceholder.typicode.com/users'
            )
            const users = await res.json()

            dispatch('sayHello')

            commit('updateUsers', users)
        }
    },
}
