import axios from "axios";

export default {
    state: {
        users:[]
    },
    getters: {
    },
    mutations: {
        updateUsers(state,users){
            state.users=users
        }
    },
    actions: {
        async fetchUsers(commit,getters,dispatch){
            const res=axios('https://jsonplaceholder.typicode.com/posts').then(res=>res.data)
            dispatch('sayHello')
            commit('updateUsers',res)
        }
    },
}
